  private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
    JFileChooser fc = new JFileChooser( System.getProperty("user.home") );
    fc.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
    fc.showOpenDialog(this);
    this.setAlwaysOnTop(true);
    this.requestFocus();
    jTextField1.setText( fc.getCurrentDirectory().getAbsolutePath() );
    jTextField1.requestFocus();
  }//GEN-LAST:event_jButton2ActionPerformed

  private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    this.setVisible(false);
    this.formWindowClosing(null);
  }//GEN-LAST:event_jButton1ActionPerformed

  private void formWindowClosing(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowClosing
    settings.setProperty("backupdir", jTextField1.getText());
    settings.setProperty("xpcomfile", jTextField3.getText());
    settings.setProperty("vboxhome", jTextField4.getText());
    settings.setProperty("librarypath", jTextField5.getText());
  }//GEN-LAST:event_formWindowClosing

