  private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
    JFileChooser fc = new JFileChooser( System.getProperty("java.home") );
    fc.setFileSelectionMode(JFileChooser.FILES_ONLY);
    fc.showOpenDialog(this);
    this.setAlwaysOnTop(true);
    this.requestFocus();
    jTextField3.setText( fc.getSelectedFile().getAbsolutePath() );
    jTextField3.requestFocus();
  }//GEN-LAST:event_jButton4ActionPerformed

  private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
    jTextField1.setText( settings.getProperty("backupdir") );
    jTextField3.setText( settings.getProperty("xpcomfile") );
    jTextField4.setText( settings.getProperty("vboxhome") );
    jTextField5.setText( settings.getProperty("librarypath") );
  }//GEN-LAST:event_formWindowOpened

  private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
    JFileChooser fc = new JFileChooser( System.getProperty("java.home") );
    fc.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
    fc.showOpenDialog(this);
    this.setAlwaysOnTop(true);
    this.requestFocus();
    jTextField4.setText( fc.getSelectedFile().getAbsolutePath() );
    jTextField4.requestFocus();
  }//GEN-LAST:event_jButton5ActionPerformed

  private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
    JFileChooser fc = new JFileChooser( System.getProperty("user.path") );
    fc.setFileSelectionMode(JFileChooser.DIRECTORIES_ONLY);
    fc.showOpenDialog(this);
    this.setAlwaysOnTop(true);
    this.requestFocus();
    jTextField5.setText( fc.getSelectedFile().getAbsolutePath() );
    jTextField5.requestFocus();
  }//GEN-LAST:event_jButton6ActionPerformed

